def.body:
get_datas.find do |i|
  i[:pos] == position
end || {pos: position, value: "", type: ""}

if SUPPORTED_TYPES.has_key?(type_req)
else
  raise("Unsupported type for write method")
end
type = SUPPORTED_TYPES[type_req]
value = (type == "s") && str.is_a?(String) ? (Crexcel::SharedString.new(str)).index.to_s : str.to_s
@datas << {pos: position, value: value, type: type}

if str.is_a?(String)
  value = (Crexcel::SharedString.new(str)).index.to_s
  type = "s"
else
  value = str.to_s
  type = ""
end
@datas << {pos: position, value: value, type: type}
